import React from 'react';
import Header from './Header';
import Main from './Main';
import Footer from './Footer';
import PopupWithForm from './PopupWithForn';
import ImagePopup from './ImagePopup';

function App() {

  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = React.useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = React.useState(false);
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = React.useState(false);

  function handleEditAvatarClick () {
      setIsEditAvatarPopupOpen(true);
  }

  function handleEditProfileClick () {
    setIsEditProfilePopupOpen(true);
  }

  function handleAddPlaceClick () {
    setIsAddPlacePopupOpen(true);
  }

  function closeAllPopups () {
    setIsEditAvatarPopupOpen(false);
    setIsEditProfilePopupOpen(false);
    setIsAddPlacePopupOpen(false);
  }

  return (
    <div className='page-container'>
      <Header />
      <Main 
      handleEditAvatar={handleEditAvatarClick}
      handleAddPlace={handleAddPlaceClick}
      handleEditProfile={handleEditProfileClick }
      />
      <PopupWithForm
        name='edit-profile'
        title='Редактировать профиль'
        buttonText='Сохранить'
        isOpen={isEditProfilePopupOpen}
        onClose={closeAllPopups}
      >
        <input
          type='text'
          className='popup__input popup__input_type_name'
          defaultValue='Жак-Ив Кусто'
          placeholder=' Введите имя'
          name='name'
          required
          minLength='2'
          maxLength='40'
          id='name-input'
        />
        <span className='popup__input-error' id='name-input-error'></span>
        <input
          type='text'
          className='popup__input popup__input_type_info'
          defaultValue='Исследователь океана'
          placeholder=' Чем вы занимаетесь?'
          name='about'
          required
          minLength='2'
          maxLength='200'
          id='info-input'
        />
        <span className='popup__input-error' id='info-input-error'></span>
      </PopupWithForm>

      <PopupWithForm name='new-card' title='Новое место' buttonText='Создать'
      isOpen={isAddPlacePopupOpen} onClose={closeAllPopups}>
        <input
          type='text'
          className='popup__input popup__input_type_title'
          defaultValue=''
          placeholder='Название'
          name='name'
          required
          minLength='1'
          maxLength='30'
          id='title-input'
        />
        <span className='popup__input-error' id='title-input-error'></span>
        <input
          type='url'
          className='popup__input popup__input_type_image-link'
          defaultValue=''
          placeholder='Ссылка на картинку'
          name='link'
          required
          id='link-input'
        />
        <span className='popup__input-error' id='link-input-error'></span>
      </PopupWithForm>

      <PopupWithForm
        name='confirm-deleting'
        title='Вы уверены?'
        buttonText='Да'
      />

      <PopupWithForm
        name='avatar'
        title='Обновить аватар'
        buttonText='Сохранить'
        isOpen={isEditAvatarPopupOpen}
        onClose={closeAllPopups}
      >
        <input
          type='url'
          className='popup__input popup__input_type_avatar'
          defaultValue=''
          placeholder='Ссылка на картинку'
          name='avatar'
          required
          id='avatar-input'
        />
        <span className='popup__input-error' id='avatar-input-error'></span>
      </PopupWithForm>

      <ImagePopup />

      <Footer />
    </div>
  );
}

export default App;
